var five = require("../lib/johnny-five.js"),
    board = new five.Board();

board.on("ready", function() {
  var val = 0;

  this.pinMode( 15, 1 );

  // Create a loop to "flash/blink/strobe" an led
  this.loop( 100, function() {
    this.digitalWrite( 15, (val = val ? 0 : 1) );
  });
});


