var five = require("../lib/johnny-five.js");

five.Board().on("ready", function() {
  var left, right, rgb, rainbow, index;

  // left = new five.Led.RGB([ 6, 5, 4 ]);
  right = new five.Led.RGB({
    pins: {
      red: 11,
      green: 10,
      blue: 9
    }
  });


  // right.on();

  rainbow = [ "FF000", "FF7F00", "00FF00", "FFFF00", "0000FF", "4B0082", "8F00FF" ];
  index = 0;

  setInterval(function() {
    if ( index + 1 === rainbow.length ) {
      index = 0;
    }
    right.color( rainbow[ index++ ] );
  }, 500);


  this.repl.inject({
    // left: left,
    right: right
  });
});
