var five = require("../lib/johnny-five.js");

five.Board().on("ready", function() {

  // Create a standard `led` hardware instance
  var led = new five.Led(this.type === "LEONARDO" ? 13 : 11);

  led.on();

  // Inject led object into REPL session
  this.repl.inject({
    five: five,
    led: led//,
    // pin: pin
  });
});
