var five = require("../lib/johnny-five"),
    board = new five.Board();

board.on("ready", function () {

  var leds = new Array(5);
  for (var i = 0; i < 5; i++) {
    leds[i] = new five.Led(i + 5);
  }

  console.log( leds );

  setInterval(function () {
    for (var i = 0; i < 5; i++) {
      var on = Math.random() > 0.5;
      leds[i][on ? "on" : "off"]();
    }
  }, 33);
});
