var five = require("../lib/johnny-five.js");

new five.Board().on("ready", function() {
  function toDegrees(value) {
    return Math.round(
      five.Fn.scale(
        value, 0, 255, 0, 180
      )
    );
  }

  var led = new five.Led("O0");
  var servo = new five.Servo({
    pin: "O1",
    type: "continuous"
  });

  this.repl.inject({
    s: servo
  });
  new five.Sensor("I0").scale(0, 255).on("data", function() {
    led.brightness(Math.round(this.value));
    servo.move( toDegrees(this.value) );
    // console.log( toDegrees(this.value) );
  });
});

