var firmata = require('../lib/firmata');

var board = new firmata.Board('/dev/tty.usbmodemfd121', function () {
	var i = 0;
	var adder = 100;
	board.pinMode(6, board.MODES.PWM);
	setInterval(function () {
		i += adder;
		board.analogWrite(6, 200);

		board.analogWrite(6, 0);

		board.analogWrite(6, 200);

		if(i >= 250 || i <= 0) {
			adder *= -1;
		}

	}, 500);
});